import{_ as l,a as i,o as e,f as a}from"./chunks/framework.x10tvMrD.js";const m=JSON.parse('{"title":"游戏讨论&创作社区","description":"","frontmatter":{"title":"游戏讨论&创作社区","editLink":false,"lastUpdated":true,"showComment":false},"headers":[],"relativePath":"projects/01-前端&全栈开发/02-游戏讨论&创作社区.md","filePath":"projects/01-前端&全栈开发/02-游戏讨论&创作社区.md","lastUpdated":1706615111000}'),t={name:"projects/01-前端&全栈开发/02-游戏讨论&创作社区.md"},o=a('<h2 id="项目盘点" tabindex="-1">项目盘点 <a class="header-anchor" href="#项目盘点" aria-label="Permalink to &quot;项目盘点&quot;">​</a></h2><h3 id="项目需求与选型" tabindex="-1">项目需求与选型 <a class="header-anchor" href="#项目需求与选型" aria-label="Permalink to &quot;项目需求与选型&quot;">​</a></h3><ul><li>项目需求 <ul><li>灵感来源 <ul><li>在浏览掘金开发者社区带来的灵感。游戏社区首版 UI 样式也是模仿掘金社区，因为我自己 <ul><li>很喜欢玩各种单机游戏</li><li>喜欢国产单机，对国产单机充满希望</li><li>是大宇的仙剑轩辕剑粉</li><li>然后之前用过贴吧，因为贴吧的删帖等规则让人摸不到头脑，然后吧务管理上也受到了不少限制 因此萌生出想要开发一个游戏社区讨论平台，同时增加同人投稿功能，这样能让玩家有一个舒适的，明确社区规则的讨论环境，也是想对自己有一个技术上的提升。</li></ul></li></ul></li><li>项目需求 <ul><li>最早是想做国产单机游戏社区，但是考虑到用户量较大，前期服务器和开发成本费用付出会比较大，因此先开发以轩辕剑为主题的社区，后续再在其基础上，扩展为国产单机社区。</li></ul></li><li>项目选型 <ul><li>第一阶段（当前阶段） <ul><li>客户端（PC WEB）开发框架选择 Vue 快速开发，使用 Vite 构建，使用 TailWindCss 来作为原子样式库，使用 ArcoDesign 作为 UI 框架</li><li>管理端开发框架与客户端的选型基本相似。</li><li>后端使用 Nestjs 实现。</li></ul></li><li>项目重构计划 <ul><li>使用 React 重构客户端（Vue、React）与管理端。</li><li>自研 Sword Design（Vue、React、移动端、小程序）框架，客户端(PC WEB)使用 Sword Design 重构。</li><li>自研编辑器框架，使用多种方案，对不同类型的文本编辑器和富文本解析进行转换，并加入表情包解析，减少对三方组件的依赖，提高开发自主性，为社区提供更多的功能。</li><li>后端启用 Golang 重置，提升后端的性能。</li></ul></li></ul></li></ul></li></ul><h3 id="做了些什么" tabindex="-1">做了些什么 <a class="header-anchor" href="#做了些什么" aria-label="Permalink to &quot;做了些什么&quot;">​</a></h3><ul><li>第一阶段（当前阶段） <ul><li>独立组件开发 <ul><li>评论、短微博输入框的组件，支持插入表情，支持文字限制</li><li>单图/多图九宫格图片组件</li><li>投票组件</li><li>日历组件</li><li>九宫格抽奖组件</li><li>@提及组件</li><li>聊天框组件，支持表情包选择和发送</li><li>markdown 组件二次开发</li></ul></li><li>权限颗粒化，实时权限管理 <ul><li>使用 casbin 实现权限控制管理，前后端分离</li></ul></li><li>消息撤回功能</li><li>小程序扫码登录功能</li></ul></li></ul>',5),s=[o];function r(u,_,n,c,d,h){return e(),i("div",null,s)}const f=l(t,[["render",r]]);export{m as __pageData,f as default};
